import React, { useState } from 'react';

const EventForm = () => {
  const [eventData, setEventData] = useState({
    id: '',
    eventName: '',
    eventDate: '',
    eventReason: '',
    eventLocation: '',
    eventTime: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEventData({ ...eventData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add logic to handle form submission, e.g., send data to backend
    console.log('Form submitted:', eventData);
  };

  return (
    <div>
      <h2>Add Event</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Event Name:
          <input type="text" name="eventName" value={eventData.eventName} onChange={handleChange} />
        </label>
        <br />
        <label>
          Event Date:
          <input type="date" name="eventDate" value={eventData.eventDate} onChange={handleChange} />
        </label>
        <br />
        <label>
          Event Reason:
          <input type="text" name="eventReason" value={eventData.eventReason} onChange={handleChange} />
        </label>
        <br />
        <label>
          Event Location:
          <input type="text" name="eventLocation" value={eventData.eventLocation} onChange={handleChange} />
        </label>
        <br />
        <label>
          Event Time:
          <input type="time" name="eventTime" value={eventData.eventTime} onChange={handleChange} />
        </label>
        <br />
        <button type="submit">Add Event</button>
      </form>
    </div>
  );
};

export default EventForm;
